      <li>
        &#x422;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x435;, &#x43d;&#x430;&#x43f;&#x440;&#x430;&#x432;&#x43b;&#x435;&#x43d;&#x43d;&#x43e;&#x435; &#x43d;&#x430; &#x43f;&#x440;&#x43e;&#x432;&#x435;&#x440;&#x43a;&#x443; &#x443;&#x441;&#x43f;&#x435;&#x448;&#x43d;&#x43e;&#x439; &#x438;&#x43d;&#x441;&#x442;&#x430;&#x43b;&#x43b;&#x44f;&#x446;&#x438;&#x438; &#x438; &#x43d;&#x430;&#x441;&#x442;&#x440;&#x43e;&#x439;&#x43a;&#x438;, &#x430; &#x442;&#x430;&#x43a;&#x436;&#x435; &#x43e;&#x431;&#x43d;&#x43e;&#x432;&#x43b;&#x435;&#x43d;&#x438;&#x44f; &#x438;&#x43b;&#x438; &#x443;&#x434;&#x430;&#x43b;&#x435;&#x43d;&#x438;&#x44f; &#x43f;&#x440;&#x43e;&#x433;&#x440;&#x430;&#x43c;&#x43c;&#x43d;&#x43e;&#x433;&#x43e; &#x43e;&#x431;&#x435;&#x441;&#x43f;&#x435;&#x447;&#x435;&#x43d;&#x438;&#x44f;. (<u>&#x41f;&#x440;&#x43e; &#x422;&#x435;&#x441;&#x442;&#x438;&#x43d;&#x433;</u>)
