        &#x421;&#x43f;&#x435;&#x446;&#x438;&#x430;&#x43b;&#x44c;&#x43d;&#x44b;&#x439; &#x442;&#x438;&#x43f; <u><b>&#x442;&#x435;&#x441;&#x442;&#x430; &#x43d;&#x430; &#xab;&#x434;&#x44b;&#x43c;&#xbb;</b></u>&#xa0;&#x434;&#x43b;&#x44f; &#x43f;&#x440;&#x438;&#x43d;&#x44f;&#x442;&#x438;&#x44f; &#x440;&#x435;&#x448;&#x435;&#x43d;&#x438;&#x44f;, &#x433;&#x43e;&#x442;&#x43e;&#x432; &#x43b;&#x438; &#x43a;&#x43e;&#x43c;&#x43f;&#x43e;&#x43d;&#x435;&#x43d;&#x442; &#x438;&#x43b;&#x438; &#x441;&#x438;&#x441;&#x442;&#x435;&#x43c;&#x430; &#x434;&#x43b;&#x44f; &#x434;&#x430;&#x43b;&#x44c;&#x43d;&#x435;&#x439;&#x448;&#x435;&#x433;&#x43e; &#x434;&#x435;&#x442;&#x430;&#x43b;&#x44c;&#x43d;&#x43e;&#x433;&#x43e; &#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44f;. &#x41e;&#x431;&#x44b;&#x447;&#x43d;&#x43e; &#x43f;&#x440;&#x43e;&#x432;&#x43e;&#x434;&#x438;&#x442;&#x441;&#x44f; &#x432; &#x43d;&#x430;&#x447;&#x430;&#x43b;&#x430; &#x444;&#x430;&#x437;&#x44b; &#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44f;. (<u>ISTQB</u>)
