<node CREATED="1389031663031" ID="ID_317729969" MODIFIED="1389698612669" TEXT="&#x411;&#x435;&#x442;&#x430;-&#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x435;">
<richcontent TYPE="NOTE"><html>
  <head>
    
  </head>
  <body>
    <p>
      <b>&#x411;&#x435;&#x442;&#x430;-&#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x435; (beta testing)</b>&#xa0; - &#x43f;&#x440;&#x43e;&#x431;&#x43d;&#x43e;&#x435; &#x438;&#x441;&#x43f;&#x43e;&#x43b;&#x44c;&#x437;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x435; &#x441; &#x43f;&#x440;&#x438;&#x432;&#x43b;&#x435;&#x447;&#x435;&#x43d;&#x438;&#x435;&#x43c; &#x43e;&#x442;&#x43e;&#x431;&#x440;&#x430;&#x43d;&#x43d;&#x44b;&#x445; &#x432;&#x43d;&#x435;&#x448;&#x43d;&#x438;&#x445; &#x43f;&#x43e;&#x43b;&#x44c;&#x437;&#x43e;&#x432;&#x430;&#x442;&#x435;&#x43b;&#x435;&#x439;. &#x424;&#x430;&#x437;&#x430; &#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44f; &#x445;&#x430;&#x440;&#x430;&#x43a;&#x442;&#x435;&#x440;&#x43d;&#x430;&#x44f; &#x434;&#x43b;&#x44f; &#x43f;&#x440;&#x43e;&#x433;&#x440;&#x430;&#x43c;&#x43c;&#x43d;&#x44b;&#x445; &#x441;&#x438;&#x441;&#x442;&#x435;&#x43c; &#x43e;&#x431;&#x449;&#x435;&#x433;&#x43e; &#x43f;&#x440;&#x438;&#x43c;&#x435;&#x43d;&#x435;&#x43d;&#x438;&#x44f; (&#x43d;&#x435; &#x440;&#x430;&#x437;&#x440;&#x430;&#x431;&#x430;&#x442;&#x44b;&#x432;&#x430;&#x435;&#x43c;&#x44b;&#x445; &#x43d;&#x430; &#x437;&#x430;&#x43a;&#x430;&#x437;).
    </p>
    <p>
      
    </p>
    <p>
      &#x412; &#x41d;&#x41f;&#x41e; &#x41a;&#x43e;&#x43c;&#x43f;&#x44c;&#x44e;&#x442;&#x435;&#x440; &#x43a; &#x434;&#x430;&#x43d;&#x43d;&#x43e;&#x43c;&#x443; &#x432;&#x438;&#x434;&#x443; &#x43c;&#x43e;&#x436;&#x43d;&#x43e; &#x43e;&#x442;&#x43d;&#x435;&#x441;&#x442;&#x438; &#x43e;&#x43f;&#x44b;&#x442;&#x43d;&#x443;&#x44e; &#x44d;&#x43a;&#x441;&#x43f;&#x43b;&#x443;&#x430;&#x442;&#x430;&#x446;&#x438;&#x44e;.
    </p>
    <p>
      &#x41f;&#x440;&#x438;&#x43c;&#x435;&#x447;&#x430;&#x43d;&#x438;&#x435;. &#x414;&#x435;&#x44f;&#x442;&#x435;&#x43b;&#x44c;&#x43d;&#x43e;&#x441;&#x442;&#x44c; &#x43e;&#x442;&#x434;&#x435;&#x43b;&#x430; &#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44f; &#x442;&#x440;&#x443;&#x434;&#x43d;&#x43e; &#x43e;&#x434;&#x43d;&#x43e;&#x437;&#x43d;&#x430;&#x447;&#x43d;&#x43e; &#x43e;&#x442;&#x43d;&#x435;&#x441;&#x442;&#x438; &#x43a; &#x430;&#x43b;&#x44c;&#x444;&#x430;- &#x438;&#x43b;&#x438; &#x431;&#x435;&#x442;&#x430;-&#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44e;, &#x442;&#x430;&#x43a; &#x43a;&#x430;&#x43a; &#x432;&#x44b;&#x43f;&#x43e;&#x43b;&#x43d;&#x44f;&#x435;&#x43c;&#x430;&#x44f; &#x43d;&#x430;&#x43c;&#x438; &#x440;&#x430;&#x431;&#x43e;&#x442;&#x430; &#x43f;&#x440;&#x43e;&#x44f;&#x432;&#x43b;&#x44f;&#x435;&#x442; &#x43f;&#x440;&#x438;&#x437;&#x43d;&#x430;&#x43a;&#x438; &#x43e;&#x431;&#x43e;&#x438;&#x445; &#x432;&#x438;&#x434;&#x43e;&#x432;.
    </p>
    <p>
      
    </p>
    <p>
      <u>&#x412;&#x430;&#x440;&#x438;&#x430;&#x43d;&#x442;&#x44b; &#x43e;&#x43f;&#x440;&#x435;&#x434;&#x435;&#x43b;&#x435;&#x43d;&#x438;&#x44f; &#x438;&#x437; &#x440;&#x430;&#x437;&#x43d;&#x44b;&#x445; &#x438;&#x441;&#x442;&#x43e;&#x447;&#x43d;&#x438;&#x43a;&#x43e;&#x432;</u>:
    </p>
    <ol>
      <li>
        &#x42d;&#x43a;&#x441;&#x43f;&#x43b;&#x443;&#x430;&#x442;&#x430;&#x446;&#x438;&#x43e;&#x43d;&#x43d;&#x43e;&#x435;&#xa0;&#xa0;&#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x435;&#xa0;&#xa0;&#x43f;&#x43e;&#x442;&#x435;&#x43d;&#x446;&#x438;&#x430;&#x43b;&#x44c;&#x43d;&#x44b;&#x43c;&#x438; &#x438;/&#x438;&#x43b;&#x438; &#x441;&#x443;&#x449;&#x435;&#x441;&#x442;&#x432;&#x443;&#x44e;&#x449;&#x438;&#x43c;&#x438; &#x43a;&#x43b;&#x438;&#x435;&#x43d;&#x442;&#x430;&#x43c;&#x438;/&#x437;&#x430;&#x43a;&#x430;&#x437;&#x447;&#x438;&#x43a;&#x430;&#x43c;&#x438; &#x43d;&#x430; &#x432;&#x43d;&#x435;&#x448;&#x43d;&#x435;&#x439; &#x441;&#x442;&#x43e;&#x440;&#x43e;&#x43d;&#x435; &#x43d;&#x438;&#x43a;&#x430;&#x43a; &#x43d;&#x435; &#x441;&#x432;&#x44f;&#x437;&#x430;&#x43d;&#x43d;&#x44b;&#x43c;&#x438; &#x441;&#xa0; &#x440;&#x430;&#x437;&#x440;&#x430;&#x431;&#x43e;&#x442;&#x447;&#x438;&#x43a;&#x430;&#x43c;&#x438;,&#xa0;&#xa0;&#x441;&#xa0;&#xa0;&#x446;&#x435;&#x43b;&#x44c;&#x44e;&#xa0;&#xa0;&#x43e;&#x43f;&#x440;&#x435;&#x434;&#x435;&#x43b;&#x435;&#x43d;&#x438;&#x44f;&#xa0;&#xa0;&#x434;&#x435;&#x439;&#x441;&#x442;&#x432;&#x438;&#x442;&#x435;&#x43b;&#x44c;&#x43d;&#x43e;&#xa0;&#xa0;&#x43b;&#x438;&#xa0;&#xa0;&#x43a;&#x43e;&#x43c;&#x43f;&#x43e;&#x43d;&#x435;&#x43d;&#x442;&#xa0; &#x438;&#x43b;&#x438;&#xa0;&#xa0;&#x441;&#x438;&#x441;&#x442;&#x435;&#x43c;&#x430; &#x443;&#x434;&#x43e;&#x432;&#x43b;&#x435;&#x442;&#x432;&#x43e;&#x440;&#x44f;&#x435;&#x442;&#xa0;&#xa0;&#x442;&#x440;&#x435;&#x431;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44f;&#x43c;&#xa0;&#xa0;&#x43a;&#x43b;&#x438;&#x435;&#x43d;&#x442;&#x430;/&#x437;&#x430;&#x43a;&#x430;&#x437;&#x447;&#x438;&#x43a;&#x430;&#xa0;&#xa0;&#x438;&#xa0; &#x432;&#x43f;&#x438;&#x441;&#x44b;&#x432;&#x430;&#x435;&#x442;&#x441;&#x44f;&#xa0;&#xa0;&#x432;&#xa0;&#xa0;&#x431;&#x438;&#x437;&#x43d;&#x435;&#x441;-&#x43f;&#x440;&#x43e;&#x446;&#x435;&#x441;&#x441;&#x44b;.&#xa0;&#xa0;&#x411;&#x435;&#x442;&#x430;-&#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x435; &#x447;&#x430;&#x441;&#x442;&#x43e; &#x43f;&#x440;&#x43e;&#x432;&#x43e;&#x434;&#x438;&#x442;&#x441;&#x44f; &#x43a;&#x430;&#x43a; &#x444;&#x43e;&#x440;&#x43c;&#x430; &#x432;&#x43d;&#x435;&#x448;&#x43d;&#x435;&#x433;&#x43e; &#x43f;&#x440;&#x438;&#x451;&#x43c;&#x43e;&#x447;&#x43d;&#x43e;&#x433;&#x43e; &#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44f; &#x433;&#x43e;&#x442;&#x43e;&#x432;&#x43e;&#x433;&#x43e; &#x43f;&#x440;&#x43e;&#x433;&#x440;&#x430;&#x43c;&#x43c;&#x43d;&#x43e;&#x433;&#x43e; &#x43e;&#x431;&#x435;&#x441;&#x43f;&#x435;&#x447;&#x435;&#x43d;&#x438;&#x44f; &#x434;&#x43b;&#x44f; &#x442;&#x43e;&#x433;&#x43e; &#x447;&#x442;&#x43e;&#x431;&#x44b; &#x43f;&#x43e;&#x43b;&#x443;&#x447;&#x438;&#x442;&#x44c; &#x43e;&#x442;&#x437;&#x44b;&#x432;&#x44b; &#x440;&#x44b;&#x43d;&#x43a;&#x430;. (<u>ISTQB</u>)
      </li>
      <li>
        &#x41f;&#x440;&#x43e;&#x431;&#x43d;&#x43e;&#x435; &#x438;&#x441;&#x43f;&#x43e;&#x43b;&#x44c;&#x437;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x435; &#x441; &#x43f;&#x440;&#x438;&#x432;&#x43b;&#x435;&#x447;&#x435;&#x43d;&#x438;&#x435;&#x43c; &#x43e;&#x442;&#x43e;&#x431;&#x440;&#x430;&#x43d;&#x43d;&#x44b;&#x445; &#x432;&#x43d;&#x435;&#x448;&#x43d;&#x438;&#x445; &#x43f;&#x43e;&#x43b;&#x44c;&#x437;&#x43e;&#x432;&#x430;&#x442;&#x435;&#x43b;&#x435;&#x439;. (<u>SWEBOK</u>)
      </li>
      <li>
        &#x424;&#x430;&#x437;&#x430; &#x442;&#x435;&#x441;&#x442;&#x438;&#x440;&#x43e;&#x432;&#x430;&#x43d;&#x438;&#x44f;, &#x445;&#x430;&#x440;&#x430;&#x43a;&#x442;&#x435;&#x440;&#x43d;&#x430;&#x44f; &#x434;&#x43b;&#x44f; &#x43f;&#x440;&#x43e;&#x433;&#x440;&#x430;&#x43c;&#x43c;&#x43d;&#x44b;&#x445; &#x441;&#x438;&#x441;&#x442;&#x435;&#x43c; &#x43e;&#x431;&#x449;&#x435;&#x433;&#x43e; &#x43f;&#x440;&#x438;&#x43c;&#x435;&#x43d;&#x435;&#x43d;&#x438;&#x44f; (&#x43d;&#x435; &#x440;&#x430;&#x437;&#x440;&#x430;&#x431;&#x430;&#x442;&#x44b;&#x432;&#x430;&#x435;&#x43c;&#x44b;&#x445; &#x43d;&#x430; &#x437;&#x430;&#x43a;&#x430;&#x437;). (<u>&#x421;&#x438;&#x43d;&#x438;&#x446;&#x44b;&#x43d; &#x438; &#x434;&#x440;.</u>)
